var table;
var timer;
var beeper;


var beepsnd;
var breaksnd;
var worksnd;

function preload() {

}
 
function setup() {
  createCanvas(500, 300);
  background(255);

  beepsnd = new Audio("data:audio/wav;base64,");  
  breaksnd = new Audio("./timetostop.wav");
  worksnd = new Audio("./workworkwork.wav");
  
  timer = createElement('p','This will show the remaining time');
  timer.center();
  
  input1 = createInput(); 
  input1.center();

  input1.position(input1.x,timer.y+2*timer.height);
  input1.attribute('placeholder','Work time (in minutes)');
  input2 = createInput();
  input2.center();
  input2.attribute('placeholder','Break time (in minutes)');

  input2.position(input2.x,input1.y+input1.height);
  button = createButton('Start work');
  button.center();
  button.position(button.x, input2.y + 1.5*input2.height);

  button.mousePressed(onbuttpress);
  
  
  button2 = createButton('Use global sync');
  button2.center();
  button2.position(button2.x, button.y + 1.5*button.height);

  button2.mousePressed(syncup);
  

  beeper = createCheckbox('Beep noise', false)
  beeper.center();
  beeper.position(beeper.x,button2.y+1.5*button2.height);
  
  info = createElement('p', 'Pressing global sync will sync you up with everybody who uses the same time inputs as you do.');
  info.style('text-align','center');
  info.center();
  info.position(info.x, beeper.y+2*beeper.height);
  
  /*
  button3 = createButton('Enable audio on Safari browser');
  button3.center();
  button3.position(button3.x, info.y + 3* info.height);
  enableAudio(button3,beepsnd);*/
}
 
var pressedon = 0;
var currentbreak = false;
function sleep(t) {
  return new Promise(function(resolve) {
      setTimeout(resolve, t)
  });
}

function fmod(a,b) {return Number((a - (Math.floor(a / b) * b)).toPrecision(8));}


var soundcount = 7, soundpressedon = 0;
var timeforwork = 0, timeforbreak = 0;
function draw() {
  var d = new Date();
  var R = currentbreak?timeforbreak:timeforwork;

  if(pressedon != 0 && d.getTime() - pressedon < R*60*1000) {
    var num = ((R*60*1000) - (d.getTime() - pressedon))/1000; //in secs
    var floating = '' + nf(fmod(num,1),0,4);
    //remove zero and dot
    floating = floating.substr(2,floating.length);
    
    var secs = floor(fmod(num,60));
    var mins = floor(fmod(num/60,60));
    var hours = floor(fmod(num/60/60,60));

    var bla =  fmod(num,1);
    
    if(!currentbreak) timer.html('Next break in: ' + hours + ':' + nf(mins,2) + ':' + nf(secs,2)  +  ':' + floating);
    else timer.html('Break for: ' + hours + ':' + nf(mins,2) + ':' + nf(secs,2)  +  ':' + floating);
  }
  
  if(pressedon != 0 && d.getTime() - pressedon > R*60*1000) {
    pressedon = d.getTime();
    currentbreak = !currentbreak;
    soundpressedon = d.getTime();
    soundcount = 0;
    if(beeper.checked()) {
      beepsnd.play();
    } else {
      if(currentbreak) breaksnd.play();
      else worksnd.play();
    }

  }
  if(soundcount < 7 && d.getTime()-soundpressedon > 200) {
    soundcount ++;
    soundpressedon = d.getTime();
    if(beeper.checked()) {
      beepsnd.play();
    } else {
      if(currentbreak) breaksnd.play();
      else worksnd.play();
    }
  }
}

function enableAudio(element, audio, onEnd){
  var callback = false,
      click    = false;

  click = function(e){
    var forceStop = function () {
          audio.removeEventListener('play', forceStop, false);
          audio.pause();
          element.removeEventListener('touchstart', click, false);
          if(onEnd) onEnd();
        },
        progress  = function () {
          audio.removeEventListener('canplaythrough', progress, false);
          if (callback) callback();
        };

    audio.addEventListener('play', forceStop, false);
    audio.addEventListener('canplaythrough', progress, false);
    try { 
      audio.play();
    } catch (e) {
      callback = function () {
        callback = false;
        audio.play();
      };
    }
  };
  element.addEventListener('touchstart', click, false);
}


function onbuttpress() {
  var d = new Date();
  if(input1.value() > 0 && input2.value() > 0) {
  currentbreak = false;
  timeforwork = float(input1.value());
  timeforbreak = float(input2.value());
  

  pressedon = d.getTime(); 
  }
  
  
  beepsnd = new Audio("data:audio/wav;base64,");  
  breaksnd = new Audio("./timetostop.wav");
  worksnd = new Audio("./workworkwork.wav");
}

function syncup () {
  var d = new Date();
  if(input1.value() > 0 && input2.value() > 0) {
    var totT = (float(input1.value()) + float(input2.value())) * 1000. * 60.;
    var timeN = d.getTime();
    
    var rest = fmod(timeN,totT);

    if(rest - float(input1.value())*1000. * 60. < 0) {
      currentbreak = false;
      pressedon = timeN - rest;
    } else {
      currentbreak = true;
      pressedon = timeN - rest + float(input1.value()) * 1000. * 60.;
    }
    timeforwork = float(input1.value());
    timeforbreak = float(input2.value());
  }
  
  
  beepsnd = new Audio("data:audio/wav;base64,");  
  breaksnd = new Audio("./timetostop.wav");
  worksnd = new Audio("./workworkwork.wav");
}